import * as d3 from 'd3';
import { db } from './firebase';
import { collection,getDocs,query, orderBy, onSnapshot,docChanges } from "firebase/firestore"; 

const dims = { height: 300, width: 300, radius: 150 };
const cent = { x: (dims.width / 2 + 5), 
               y: (dims.height / 2 + 5)};
const svgWidth = dims.width + 150;
const svgHeight = dims.height + 150;

function Graph () {
       //define pie from d3 
        const pie = d3.pie()
                      .sort(null)
                      .value(d => d.cost);
        //this creates start and end angle within 2pi range
        const angles = pie([
            { name: 'rent', cost: 500 },
            { name: 'bills', cost: 300 },
            { name: 'gaming', cost: 200 }
        ])
        //this creates a path for each angle(eg : arcPath(angles[0])= M9.184850993605149e-15...)
        const arcPath = d3.arc()
                          .outerRadius(dims.radius)
                          .innerRadius(dims.radius / 2);
        // update function
        const graph = d3.select('svg')
                        .append('g');
        const update = (data) => {
            const paths = d3.selectAll('path')
                            .data(pie(data));
              // console.log(paths);
            paths.enter()
                 .attr('class', 'arc')
                 .data('d', arcPath)
                 .attr('stroke', 'white')
                 .attr('stroke-width', 3);
        };
    // data array and firestore
    var data = [];  
         
         const citiesRef = collection(db, "expenseTracker");
         const q = query(citiesRef, orderBy("cost"));
        // const querySnapshot = getDocs(q);
         //console.log(querySnapshot.onSnapshot)
         const unsubscribe = onSnapshot(q, (querySnapshot) => {
          
            const changes = querySnapshot.docChanges();

            changes.forEach((change) => {
                const doc = {...change.doc.data(), id: change.doc.id};
               // console.log("change type: " +change.type+ "  change id: " + change.doc.id)
            
             switch (change.type) {
                case 'added':
                    data.push(doc);
                    break;
                case 'modified':
                    const index = data.findIndex(item => item.id == doc.id);
                    data[index] = doc;
                    break;
                case 'removed':
                    data = data.filter(item => item.id !== change.doc.id);
                    break;
                default:
                    break;
             } 
            });
            update(data);
          });
    
        return(
        <>
        <g transform={`translate(${cent.x}, ${cent.y})`}>
           <svg width={svgWidth} height={svgHeight}>
               <path></path>
           </svg>
        </g>
        </>
        );
  }

  export default Graph;
