import * as d3 from 'd3';
import { db } from './firebase';
import { collection,query, orderBy, onSnapshot,docChanges } from "firebase/firestore"; 

const dims = { height: 300, width: 300, radius: 150 };
const cent = { x: (dims.width / 2 + 5), y: (dims.height / 2 + 5)};

const svg = d3.select('.canvas')
              .append('svg')
              .attr('width', dims.width + 150)
              .attr('height', dims.height + 150);
  
const graph = svg.append('g')
                 .attr("transform", `translate(${cent.x}, ${cent.y})`);

const pie = d3.pie()
              .sort(null)
              .value(d => d.cost);// the value we are evaluating to create the pie angles

const arcPath = d3.arc()
                  .outerRadius(dims.radius)
                  .innerRadius(dims.radius / 2);
//set random generated colour 
const colour = d3.scaleOrdinal(d3["schemeSet3"]);

// update function
const update = (data) => {
    // update colour scale domain
    colour.domain(data.map(d => d.name));
   // join enhanced (pie) data to path elements
    const paths = graph.selectAll('path')
                       .data(pie(data));
       // console.log(paths);

    paths.enter()
         .append('path')
         .attr('class', 'arc')
         .attr('d', arcPath)
         .attr('stroke', '#fff')
         .attr('stroke-width', 3)
         .attr('fill', d => colour(d.data.name));;
};

function Graph () {
   
    var data = [];  
         
    const dataRef = collection(db, "expenseTracker");
    const q = query(dataRef, orderBy("cost"));
    // const querySnapshot = getDocs(q);
    //console.log(querySnapshot.onSnapshot)
    const unsubscribe = onSnapshot(q, (querySnapshot) => {
            const changes = querySnapshot.docChanges();
            changes.forEach((change) => {
                const doc = {...change.doc.data(), id: change.doc.id};
                // console.log("change type: " +change.type+ "  change id: " + change.doc.id)
                switch (change.type) {
                    case 'added':
                       data.push(doc);
                       break;
                    case 'modified':
                        const index = data.findIndex(item => item.id == doc.id);
                        data[index] = doc;
                        break;
                    case 'removed':
                        data = data.filter(item => item.id !== change.doc.id);
                        break;
                    default:
                        break;
                } 
            });
            update(data);
    });
    
    return(
     
        <div class="canvas" id="canvas"></div>
   
    );
}

  export default Graph;
