import { db } from './firebase';
import {collection, addDoc } from "firebase/firestore"; 

const form = document.querySelector('form');
const name = document.querySelector('#name');
const cost = document.querySelector('#cost');
const error = document.querySelector('#error');

form.addEventListener('submit', (e) => {

  e.preventDefault();

  if (name.value && cost.value) {

    const item = { 
      name: name.value, 
      cost: parseInt(cost.value) 
    };
    async function docRef() {
      await addDoc(collection(db, "expenseTracker"), {
      name: name.value,
      cost: parseInt(cost.value)
    });
  }
    console.log(docRef());

  } else {
    error.textContent = 'Please enter values before submitting';
  }

});
function App() {
  return (
    <>
      <header class="indigo darken-1 section">
    <h2 class="center white-text">Expense Tracker</h2>
    <p class="flow-text grey-text center text-lighten-2">Monthly expense tracker</p>
  </header>
  <div class="container section">
    <div class="row">
      <div class="col s12 m6">
        <form class="card z-depth-0">
          <div class="card-content">
            <span class="card-title indigo-text">Add expenses:</span>
            <div class="input-field">
              <input type="text" id="name" />
              <label for="name">Cateory</label>
            </div>
            <div class="input-field">
              <input type="text" id="cost" />
              <label for="cost">Cost</label>
            </div>
            <div class="input-field center">
              <button class="btn-large pink white-text">Add Item</button>
            </div>
            <div class="input-field center">
              <p id="error" class="red-text"></p>
            </div>
          </div>
        </form>
      </div>
      <div class="col s12 m5 push-m1">
        <div class="canvas"></div>
      </div>
    </div>
  </div>

  </>
  );
}

export default App;
